select Date(TimeOfSignal), sum(s) from (
   select Date(TimeOfSignal) as TimeOfSignal, sum(ProfitPotentialPercent) as s from ChartPatternSignal where IsPriceTargetMet=1 group by Date(TimeOfSignal)
   UNION
   select Date(TimeOfSignal) as TimeOfSignal, sum(ProfitPercentAtSignalTargetTime) as s from ChartPatternSignal where IsPriceTargetMet=0 and MaxLossPercent<5 group by Date(TimeOfSignal)
   UNION
   select Date(TimeOfSignal) as TimeOfSignal, sum(-5) as s from ChartPatternSignal where IsPriceTargetMet=0 and MaxLossPercent>5 group by Date(TimeOfSignal))
group by Date(TimeOfSignal)
order by Date(TimeOfSignal);

